import UIKit

protocol ArtistsListViewDelegate where Self: UIViewController {
  
  func itemPressed(index: Int)
  func nextPage(page:Int)
}

final class ArtistsListView: BaseView {
  
  weak var delegate: ArtistsListViewDelegate?
  
  
  let minItemsToReload: Int = 2
  var reloadingList: Bool = false
  var page = 1
  
  @IBOutlet weak var artistsTableView: UITableView!
  private var dataSource: ArtistsListModel.ViewDataSource?

  override init(frame: CGRect) {
    super.init(frame: frame)
    setupUIComponents()
  }
  
  required init?(coder aDecoder: NSCoder) {
    super.init(coder: aDecoder)
    setupUIComponents()
  }
  func updateDataSource(with dataSource: ArtistsListModel.ViewDataSource) {
    self.dataSource = dataSource
    self.artistsTableView.reloadData()
    self.reloadingList = false
  }
  func reloadUIComponents() {
    self.artistsTableView.tableFooterView = UIView()
    self.artistsTableView.reloadData()
  }
  func retryRequest() {
    delegate?.nextPage(page: page)
  }
  
}

private extension ArtistsListView {
  func setupUIComponents() {
    artistsTableView.allowsMultipleSelection = false
    self.reloadUIComponents()
    self.artistsTableView.register(R.nib.artistTableViewCell)
  }
}
// MARK: - UITableview
extension ArtistsListView:  UITableViewDataSource, UITableViewDelegate {
  func numberOfSections(in tableView: UITableView) -> Int {
    1
  }
  func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
    if let data = dataSource {
      return data.items.count
    }
    return 0
  }
  
  func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
    guard let cell = tableView.dequeueReusableCell(withIdentifier: R.reuseIdentifier.artistTableViewCell, for: indexPath) else {
      return UITableViewCell()
    }
    if let data = dataSource  {
      cell.prepareCell(with: data.items[indexPath.row])
    }
    return cell
  }
  
  func tableView(_ tableView: UITableView, didSelectRowAt indexPath: IndexPath) {
    delegate?.itemPressed(index: indexPath.row)
  }
  
  func tableView(_ tableView: UITableView, willDisplay cell: UITableViewCell, forRowAt indexPath: IndexPath) {
      
      if tableView == artistsTableView {
        if let data = dataSource  {
          if !data.items.isEmpty {
            if !self.reloadingList &&  data.items.count - (indexPath as NSIndexPath).row < minItemsToReload {
                  self.reloadingList = true
                  page += 1
                  delegate?.nextPage(page: page)
              }
          }
        }
          
      }
  }
}
